import React ,{Component} from "react";

export default class Main extends  Component{

    state = {
        initView : true ,
        loading : false ,
        user : null ,
        errorMsg : null
    }

    render() {
        const {initView , loading , user , errorMsg} = this.state

        if(initView){
            return <h2>Enter name to Search!!!</h2>
        }
        else if(loading){
            return  <h2>正在搜索中！！！</h2>
        }
        else if(errorMsg){
            return <h2>{errorMsg}</h2>
        }
        else{
            return(
                <div className="row">
                    {

                        <div className = "card">
                            <a href="https://github.com.reactjs" target='_blank'>
                                <img src="logo192.png" style={{width : 100}}/>
                            </a>
                            <p className = "card-text">react.js</p>
                        </div>

                    }
                </div>
            )
        }
    }
}
